import React from 'react';

import { Container, Row, Col, Nav } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCalendarAlt, faDumbbell, faGlobe, faHome, faUser, faUserTie } from '@fortawesome/free-solid-svg-icons';

import { CurrentUserState } from '../../reducers/auth';
import { User } from '../../models/User';

import '../../utils/Style.scss';

interface SBProps {
  currentUser?: CurrentUserState;
  handleSideBarClick: Function;
  active: string;
}

interface SBState {
  currentUser?: CurrentUserState;
  user?: User;
}

class SideBar extends React.Component<SBProps, SBState> {
  state = {
    user: {} as User,
  };

  onSideBarClick = (active: string) => {
    this.props.handleSideBarClick(active);
  };

  render() {
    const user = { ...this.state.user };
    const active = this.props.active;
    return (
      <Container>
        <div className="profile-sidebar">
          <div className="profile-userpic">
            <FontAwesomeIcon icon={faUserTie} size="6x" />
          </div>
          <div className="profile-usertitle">
            <div className="profile-usertitle-name">{user.firstName + ' ' + user.lastName}</div>
            <div className="profile-usertitle-job">{user.userType}</div>
          </div>
          <div className="profile-useritems">
            <Row>
              <Col>
                <Nav.Item as="a" onClick={() => this.onSideBarClick('profile')}>
                  Profile
                </Nav.Item>
                |<Nav.Item as="a">Logout</Nav.Item>
              </Col>
            </Row>
          </div>
          <div className="profile-usermenu">
            <Nav className="flex-column" as="ul">
              <Nav.Item
                as="li"
                className={active === 'dashboard' ? ' active' : ''}
                onClick={() => this.onSideBarClick('dashboard')}
              >
                <Nav.Link as="a">
                  <Row>
                    <Col md={1}>
                      <FontAwesomeIcon icon={faHome} />
                    </Col>
                    <Col>Dashboard</Col>
                  </Row>
                </Nav.Link>
              </Nav.Item>
              <Nav.Item
                as="li"
                className={active === 'saved_gyms' ? ' active' : ''}
                onClick={() => this.onSideBarClick('saved_gyms')}
              >
                <Nav.Link as="a">
                  <Row>
                    <Col md={1}>
                      <FontAwesomeIcon icon={faDumbbell} />
                    </Col>
                    <Col>Saved Gyms</Col>
                  </Row>
                </Nav.Link>
              </Nav.Item>
              <Nav.Item
                as="li"
                className={active === 'find_gyms' ? ' active' : ''}
                onClick={() => this.onSideBarClick('find_gyms')}
              >
                <Nav.Link as="a">
                  <Row>
                    <Col md={1}>
                      <FontAwesomeIcon icon={faGlobe} />
                    </Col>
                    <Col>Find Gyms</Col>
                  </Row>
                </Nav.Link>
              </Nav.Item>
              <Nav.Item
                as="li"
                className={active === 'schedule' ? ' active' : ''}
                onClick={() => this.onSideBarClick('schedule')}
              >
                <Nav.Link as="a">
                  <Row>
                    <Col md={1}>
                      <FontAwesomeIcon icon={faCalendarAlt} />
                    </Col>
                    <Col>Schedule</Col>
                  </Row>
                </Nav.Link>
              </Nav.Item>
              <Nav.Item
                as="li"
                className={active === 'manage_membership' ? ' active' : ''}
                onClick={() => this.onSideBarClick('manage_membership')}
              >
                <Nav.Link as="a">
                  <Row>
                    <Col md={1}>
                      <FontAwesomeIcon icon={faUser} />
                    </Col>
                    <Col>Manage Membership</Col>
                  </Row>
                </Nav.Link>
              </Nav.Item>
            </Nav>
          </div>
        </div>
      </Container>
    );
  }
}

export default SideBar;
