import React, { Fragment } from 'react';
import { Navbar, Nav, Container, Button } from 'react-bootstrap';
import { NavLink } from 'react-router-dom';
import { TokenUtils } from '../../utils';
import { Token } from '../../models/Token';
import { connect } from 'react-redux';

const currentUser = TokenUtils.getToken();

const logout = TokenUtils.removeItem();

interface NBProps {
    currentUser: Token;
}

interface NBState {
    currentUser: Token;
}

class NavigationBar extends React.Component<NBProps, {}> {
    render() {
        const { firstName, lastName } = this.props.currentUser;
        return (
            <Navbar collapseOnSelect expand='lg' className="navbar">
                <Container>
                    <Navbar.Brand className="navbar__logo" as={NavLink} to='/'>jobs portal</Navbar.Brand>
                    <Navbar.Toggle area-controls='responsive-navbar-nav' />
                    <Navbar.Collapse className='navbar__auth-nav justify-content-end' id='responsive-navbar-nav'>
                        <Nav>
                            {currentUser ? (
                                <Fragment>
                                    Welcome
                                    <Nav.Link as={NavLink} className='navbar__auth-nav--item' to='/' exact>
                                        {firstName} {lastName}
                                    </Nav.Link>
                                    <Nav.Link as={Button} variant='link' className='navbar__auth-nav--item' onClick={logout}>
                                        Logout
                                    </Nav.Link>
                                </Fragment>
                            ) : (
                                <Fragment>
                                    <Nav.Link as={NavLink} className='navbar__auth-nav--item' to='/sign-in' exact>
                                        Sign In
                                    </Nav.Link>
                                    <Nav.Link as={NavLink} className='navbar__auth-nav--item' to='/jobs-posters' exact>
                                        For Employers
                                    </Nav.Link>
                                </Fragment>
                            )}
                        </Nav>
                    </Navbar.Collapse>
                </Container>
            </Navbar>
        )
    }
}

const mapStateToProps = (state: NBState) => ({
    currentUser: state.currentUser,
});

export default connect(mapStateToProps, null)(NavigationBar);
