import { Token } from "../../models/Token";

class TokenUtils {
  sessionStorage = window.sessionStorage;

  setToken(value: Token): void {
    this.sessionStorage.setItem('token', JSON.stringify(value));
  }

  getToken(): Token {
    const tokenAsString = this.sessionStorage.getItem('token') as string;
    return JSON.parse(tokenAsString);
  }

  removeItem(): void {
    this.sessionStorage.removeItem('token');
  }
}

export default new TokenUtils();
